import { useState, createContext, useContext, useEffect } from "react";
//import React from "react"
import "./Componenets.css";

const DEBUG = true;
const border = {border:"thick solid black"};
const curPageContext = createContext(); // Context for the 1st column/screen.
const curPageContext2 = createContext(); // Context for the 2nd column/screen.

export default function TestPage(params)  {
  return (<></>)

}

export function EnterPage() {
  const {curPage, setCurPage} = useContext(curPageContext);
  return (DEBUG) ? (
      <>
        <div style={border}>
          <p>Current Page: "{curPage}"</p>
          <input type="text" onChange={(e)=>setCurPage(e.target.value)}/><br></br>
          <br></br>
        </div> 
      </>
    ) : (<></>)
}

export function EnterPage2() {
  const {curPage2, setCurPage2} = useContext(curPageContext2);
  return (DEBUG) ? (
      <>
        <div style={border}>
          <p>Current Page: "{curPage2}"</p>
          <input type="text" onChange={(e)=>setCurPage2(e.target.value)}/><br></br>
          <br></br>
        </div> 
      </>
    ) : (<></>)
}

export function AppComponents(params) {
  
  const [curPage, setCurPage] = useState("all");
  const [curPage2, setCurPage2] = useState("all");
  return (
    <>
      <curPageContext.Provider value={{curPage, setCurPage}}>
        <div id="testDeviceBox" class="testDeviceBox">
          <EnterPage />
        </div>
      </curPageContext.Provider>
      <curPageContext2.Provider value={{curPage2, setCurPage2}}>
        <div id="testDeviceBox2" class="testDeviceBox">
          <EnterPage />
        </div>
      </curPageContext2.Provider>
      <curPageContext.Provider value={{curPage, setCurPage}}>
        <div>
          <div id="deviceBox1" class="deviceBox">
            <Page1  />
            <Page2  />
          </div>
        </div>
      </curPageContext.Provider>
      <curPageContext2.Provider value={{curPage, setCurPage}}>
          <div id="deviceBox2" class="deviceBox">
            <PageA  />
          </div>
      </curPageContext2.Provider>
    </>
  );
}

export function Page1(params)  {
  const {curPage, setCurPage} = useContext(curPageContext);
  const [serialNo, setSerialNo] = useState("");
  useEffect(()=>{
    fetch("/api?classname=pprog&param=serialNo")
    .then((res) => {return res.json()})
    .then((data)=>{if(data && data.curValue != undefined) setSerialNo(data.curValue)})
  }, []);

  function handleExit() {setCurPage("Page 1");}

  return (curPage.toLowerCase().replace(" ","") === "page1" || curPage.toLowerCase().replace(" ","") === "all") 
      ? (<>
        <div style={border}>
          <p>Current Page: "{curPage}"</p>
          <form>
            <p>Serial Number: {serialNo}</p>
            <button type="button" onClick={handleExit}>Exit</button>
          </form>
          <br></br>
        </div>
      </>)
    : (<></>)
}

export function Page2(params)  {
  const {curPage, setCurPage} = useContext(curPageContext);
  const [name, setName] = useState("");
  useEffect(()=>{
    fetch("/api?classname=patient&param=name")
    .then((res) => {return res.json()})
    .then((data)=>{if(data && data.curValue != undefined) setName(data.curValue)})
  }, []);

  function handleExit() {setCurPage("Page 2");}

  return (curPage.toLowerCase().replace(" ","") === "page2" || curPage.toLowerCase().replace(" ","") === "all") 
      ? (<>
        <div style={border}>
          <p>Current Page: "{curPage}"</p>
          <form>
            <p>Patient Name: {name}</p>
            <button type="button" onClick={handleExit}>Exit</button>
          </form>
          <br></br>
        </div>
      </>)
    : (<></>) 
}


export function PageA(params)  {
  const {curPage, setCurPage} = useContext(curPageContext);
  const [name, setName] = useState("");
  useEffect(()=>{
    fetch("/api?classname=patient&param=name")
    .then((res) => {return res.json()})
    .then((data)=>{if(data && data.curValue != undefined) setName(data.curValue)})
  }, []);

  function handleExit() {setCurPage("Page A");}

  return (curPage.toLowerCase().replace(" ","") === "pagea" || curPage.toLowerCase().replace(" ","") === "all") 
      ? (<>
        <div style={border}>
          <p>Current Page: "{curPage}"</p>
          <form>
            <p>Patient Name: {name}</p>
            <button type="button" onClick={handleExit}>Exit</button>
          </form>
          <br></br>
        </div>
      </>)
    : (<></>) 
}
